#include <stdio.h>
#include <string.h>

void PrintNum(char s[], int n) {
    for (int i = 0; i < n; i++)
        printf("%c", s[i]);
}

void comp(char a[], char b[]) {
    int maior = strlen(a) > strlen(b) ? strlen(a) : strlen(b);

    for (int i = 3; i < maior; i++)
        if (a[i] != b[i]) {
            printf("Diferenca na posicao %d; precisao de %d casas\n", i - 1, i - 2);
            printf("Casas ate a diferenca: ");
            PrintNum(a, i);
            return;
        }
}

int main() {
    // insere o numero de euler gerado pelo programa em resultado
    char resultado[20000] = "";
    char referencia[] = "2.";

    comp(resultado, referencia);

    return 0;
}
